where id = #{houseId}

mapper.select:
select

from ershoufang_lianjia where 1=1

    create_time  >= #{createTimeMin}


    and create_time <=   #{createTimeMax}


    and title like "%${title}%"

order by ${orderBy} ${direction}

    limit #{start}, #{length}


    limit 1,10

select
count(id)
from ershoufang_lianjia where 1=1

    create_time  >= #{createTimeMin}


    and create_time <=   #{createTimeMax}


    and title like "%${title}%"
